package com.schilings.neiko.samples.mail;

import com.schilings.neiko.autoconfigure.mail.sender.MailSender;
import org.springframework.boot.SpringApplication;
import org.springframework.boot.autoconfigure.SpringBootApplication;
import org.springframework.context.ConfigurableApplicationContext;

@SpringBootApplication
public class Application {

	public static void main(String[] args) {
		ConfigurableApplicationContext context = SpringApplication.run(Application.class, args);

		MailSender sender = context.getBean(MailSender.class);
		sender.sendTextMail("Subject", "测试一条邮件发送的信息，并没有什么内容", "1146830743@qq.com");
		sender.sendHtmlMail("Subject", "<h1>真不错哦</h1>"
				+ "<img src='data:image/jpeg;base64,'></img> ",
				"1146830743@qq.com");
	}

}
